import React from 'react';
import { Link, LabelledLink, Labelled } from '../EzLink/EzLink.types';
declare type TabType = Labelled | (Labelled & Link);
declare type Changeable = {
    onChange: (tab: TabType) => void;
};
declare type SubNavProps<T> = (T extends Link ? Partial<Changeable> : Changeable) & {
    tabs: T[];
    selected?: T;
};
declare type SubNav = SubNavProps<TabType>;
declare type HeaderProps = {
    actions?: React.ReactNode;
    breadcrumb?: LabelledLink;
    status?: React.ReactNode;
    title: string;
    subnav?: SubNav;
};
/**
 * EzPageHeader is used to build the outer structure of a page including the page title and associated actions.
 */
declare const EzPageHeader: React.FC<HeaderProps>;
/**
 * @component
 */
export default EzPageHeader;
